export default {
  methods: {
    setSort(sort) {
      if (sort === "Relevant") {
        this.sortNone();
      } else if (sort === "A-Z") {
        this.sortAlphabetical();
      } else if (sort === "Popular") {
        this.sortPopular();
      } else if (sort === "Newest") {
        this.sortNewest();
      } else {
        this.sortNone();
      }
    },
    sortNone() {
      this.$store.commit(`${this.module}/sort`, "Relevant");
      this.$store.commit(`${this.module}/order`, null);
    },
    sortAlphabetical() {
      this.$store.commit(`${this.module}/sort`, "A-Z");
      this.$store.commit(`${this.module}/order`, "asc");
    },
    sortPopular() {
      this.$store.commit(`${this.module}/sort`, "Popular");
      this.$store.commit(`${this.module}/order`, "desc");
    },
    sortNewest() {
      this.$store.commit(`${this.module}/sort`, "Newest");
      this.$store.commit(`${this.module}/order`, "desc");
    }
  },
  computed: {
    sort() {
      return this.$store.state[this.module].options.sort;
    },
    order() {
      return this.$store.state[this.module].options.order;
    },
    sortOptions() {
      return Object.keys(this.sort);
    },
    sortSelection() {
      const [selection] = Object.entries(this.sort)
        .filter(entry => entry[1].selected)
        .map(entry => entry[0]);

      return selection;
    }
  }
};

export default {
  methods: {
    fetchNext() {
      if (this.offset + this.limit < this.count) {
        let next = this.offset + this.limit;
        this.$store.commit(`${this.module}/offset`, next);
        this.$store.commit(`${this.module}/is_loading_next`);

        setTimeout(() => {
          this.$store.dispatch(`${this.module}/fetch_data`);
        }, 1000);
      }
    }
  },
  computed: {
    count() {
      return this.$store.state[this.module].count;
    },
    limit() {
      return this.$store.state[this.module].options.limit;
    },
    offset() {
      return this.$store.state[this.module].options.offset;
    },
    isLoadingNext() {
      return this.$store.state[this.module].loading_next;
    }
  }
};

export default {
  methods: {
    setSearchTerm(searchTerm) {
      this.$store.commit(`${this.module}/filter`, searchTerm);
    },
    clearSearchTerm() {
      this.$store.commit(`${this.module}/clear_filter`);
    }
  },
  computed: {
    searchTerm() {
      return this.$store.state[this.module].options.filter;
    }
  }
};

